// Los clientes deben tener identificador y la lista de tiendas en las que han estado
// De las tiendas se tiene la ip o hostname y puerto si es necesario, y el tipo
class Shop {
    constructor(forum, lproducts){
        this.type = "js"; // shop type to send and receive
        this.products = {}; // Products dictionary inizalizer
        this.forum = forum; // shop avalaible space for clients
        this.isOpen = true; // shop initial state
        this.clientes = {}; // Client list, index by idCliente, stores {'idTienda': {direccion: 'd', tipo: 'php/py/js'}, ...}

        for(let i = 0; i < lproducts.length; i++){
            this.products[lproducts[i].nombre] = lproducts[i].cantidad;
        }
    }

    get isFull(){
        return Object.keys(this.clientes).length >= this.forum;
    }

    // A client makes a petition to know other shops
    knownShops(clientID){
        var kshops = []; // [{idTienda: 'a', direccion: 'd', tipo: 'php/py/js'}, ...]
        var auxshops = {};

        for (let client in this.clientes) {
            auxshops = Object.assign({}, this.clientes[client], auxshops);
        }

        for (let shop in auxshops) {
            if (this.clientes[clientID].shops[shop] === undefined) {
                kshops.push({idTienda: shop, direccion: auxshops[shop].direccion, tipo: auxshops[shop].tipo});
            }
        }

        return kshops;
    }

    isInside(clientID) {
        return this.clientes[clientID] !== undefined;
    }

    clientJoin(idCliente, kshops) {
        dshops = {};

        for(let i = 0; i < kshops.length; i++){
            dshops[kshops[i].idTienda] = {direccion: kshops[i].direccion, tipo: kshops[i].tipo};
        }

        this.clientes[idCliente] = dshops;
    }

    clientLeave(idCliente) {
        delete this.clientes[idCliente];
    }

    // client petition to buy some items
    sellItems(list_products){ // [{nombre: 'a', cantidad: n}, ..., {nombre: 'z', cantidad: m}]
        var newlist = [];

        // iterate client list
        for(let i = 0; i < list_products.length; i++){
            if(this.products[list_products[i].nombre] !== undefined){
                var ns = Math.max(this.products[list_products[i].nombre] - list_products[i].cantidad, 0);

                newlist.push({nombre: list_products[i].nombre, cantidad: this.products[list_products[i].nombre] - ns});

                this.products[list_products[i].nombre] = ns;
                if (ns === 0) {
                    delete this.products[list_products[i].nombre];
                }
            }
        }

        if (Object.keys(this.products).length === 0) {
            this.isOpen = false;
        }

        return newlist;
    }
}

