class Agent { //Agente gen√©rico
    constructor(id){
        this.id = id;
        this.messages = [];
    }
    sendMessage(message, destId) { // Funcion para enviar un mensaje
        var agentDict = flow.get("agentDict");
        if (agentDict.hasOwnProperty(destId)){
            agentDict[destId].receiveMessage(message, this.id, Date.now());
            return true;
        }
        else
        {
            var send = flow.get("send");
            return send(message, destId, this.id, Date.now());
        }
    }
    receiveMessage(message, senderId, time) {
        this.messages.push({'sender': senderId, 'timestamp': time, 'message': message});
    }
}


