class TimeAgent extends Agent {
    constructor(id) {
        super(id);
    }
    getTime(){
        var date = new Date();
        // Hours part from the timestamp
        var hours = date.getUTCHours();
        // Minutes part from the timestamp
        var minutes = "0" + date.getUTCMinutes();
        // Seconds part from the timestamp
        var seconds = "0" + date.getUTCSeconds();

        // Will display time in 10:30:23 format
        var formattedTime = hours + ':' + minutes.substr(-2) + ':' + seconds.substr(-2);

        return formattedTime;
    }
    receiveMessage(message, senderId, time) {
        super.receiveMessage (message, senderId, time);
        super.sendMessage (this.getTime(), senderId);
    }
}

function addAgent(name) { // Dar de alta un agente en el contenedor
    var Agent = flow.get("Agent");
    var TimeAgent = flow.get("TimeAgent");
    var lastId = flow.get("lastId");
    var hostname = flow.get("hostname");
    var port = flow.get("port");
    var agentDict = flow.get("agentDict");
    var dirTable = flow.get("dirTable");

    var time = Date.now().toString();
    var newId = hostname.concat(":").concat(port).concat(":").concat(time).concat(":").concat(lastId.toString());
    if (lastId === 0)
    {
        ag = new TimeAgent(newId, name);
    }
    else
    {
        ag = new Agent(newId, name);
    }
    lastId++;
    agentDict[newId] = ag;
    dirTable[newId] = {"hostname": hostname, "port": port}

    flow.set("lastId", lastId);
    flow.set("agentDict", agentDict);
    flow.set("dirTable", dirTable);
}

function remAgent(id) {
    var agentDict = flow.get("agentDict");

    delete agentDict[id];

    flow.set("agentDict", agentDict);
}

function send(message, destId, senderId, time)
{
    var dirTable = flow.get("dirTable");
    if (dirTable.hasOwnProperty(destId)){
        return [dirTable[destId], {'message': message, 'destId': destId, 'senderId': senderId, 'timestamp': time}];
    }
    return false;
}

